export const drawFunction = (ctx: CanvasRenderingContext2D) => {
  ctx.fillStyle = 'red';
  ctx.fillRect(0, 0, 100, 100);
};

interface candleToDraw {
  open: number;
  high: number;
  low: number;
  close: number;
  indicators: {
    revBar: 'buy' | 'sell';
    fractal: 'up' | 'down';
    alligator: {
      jaw: number;
      teeth: number;
      lips: number;
    };
  };
}

interface CandleCanvas {
  width: number;
  height: number;
  min: number;
  max: number;
  candlesShown: number;
}

interface CandleMountPoints {
  above: {
    first: number;
    second: number;
  };
  below: {
    first: number;
    second: number;
  };
}

class Candle2D {
  open: number;
  close: number;
  low: number;
  high: number;
  mountPoints: CandleMountPoints;

  constructor(
    originalOpen: number,
    originalClose: number,
    originalLow: number,
    originalHigh: number,
    candleCanvas: CandleCanvas
  ) {
    this.open = this.getPoint(originalOpen, candleCanvas);
    this.close = this.getPoint(originalClose, candleCanvas);
    this.low = this.getPoint(originalLow, candleCanvas);
    this.high = this.getPoint(originalHigh, candleCanvas);
  }
  // private arrow function with original point as an argument
  private getPoint = (originalPoint: number, candleCanvas: CandleCanvas) => {
    const point =
      ((candleCanvas.max - originalPoint) /
        (candleCanvas.max - candleCanvas.min)) *
      candleCanvas.height;
    return point;
  };
  private getMountPoints = () => {
    const above = {
      first: this.open,
      second: this.close,
    };
    const below = {
      first: this.close,
      second: this.open,
    };
    this.mountPoints = {
      above,
      below,
    };
  };
}
